## ---- echo=TRUE---------------------------------------------------------------
library (akc)
rj_bib = readRDS ("./rj_bib.rds")
rj_bib


## ---- echo=TRUE---------------------------------------------------------------
# import the user-defined dictionary
rj_user_dict = readRDS ("./rj_user_dict.rds")
rj_user_dict


## ----echo=TRUE----------------------------------------------------------------
rj_dict = make_dict (rj_user_dict$keyword)


## ----echo=TRUE----------------------------------------------------------------
rj_extract_keywords = rj_bib %>% 
  keyword_extract (id = "id",text = "abstract",dict = rj_dict)


## ----echo=TRUE----------------------------------------------------------------
rj_cleaned_keywords = rj_extract_keywords %>% 
  keyword_clean () %>% 
  keyword_merge ()


## ----echo=TRUE----------------------------------------------------------------
rj_network = rj_cleaned_keywords %>% 
  keyword_group ()


## ----fig4,echo=TRUE,out.width = "100%", fig.cap="Network visualization for knowledge classification of R Journal (2009-2021). The keywords were automatically classified into three groups based on Fastgreedy algorithm. Only the top 10 keywords by frequency are displayed in each group.",fig.align='center'----
rj_network %>% 
  keyword_vis ()


## ----fig5,echo=TRUE,out.width = "100%", fig.cap="Focus on one cluster of the knowledge network of R journal (2009-2021). Top 20 keywords by frequency are shown in the displayed group.",fig.align='center'----
rj_network %>% 
  keyword_network (group_no = 1,max_nodes = 20) 


## ----table1,echo=TRUE---------------------------------------------------------
rj_table = rj_network %>% 
  keyword_table () 


